$(document).ready(function () {
	url = 'http://localhost/native-optimize/public/api';
	// url = 'https://app.ads4xxx.com/api';
	wg = $('#banner51').data('wg');
	if (wg === null) {
		console.log('Native Optimize não encontrado.');
		return;
	}
	var cont = Cookies.get('banner51');
	if (cont) {
		cont++;
	} else {
		cont = 1;
	}
	var date = new Date();
	date.setTime(date.getTime() + (60 * 60 * 1000));
	Cookies.set('banner51', cont, {expires: date, path: '/'});
	$.ajax({
		dataType: "json",
		accepts: "application/json",
		method: 'GET',
		url: url + '/random_creatives?wg=' + wg + '&cont=' + cont
	}).done(function (x) {
		if (x != null && x.length> 0) {
			v = x[0];
			a = $('<a>', {
				title: '',
				href: url + '/clicks?click_id=' + v.click_id + '&ct=' + v.hashid + '&wg=' + wg + '&cp=' + v.campaign_id,
				target: '_blank',
			});
			img = $('<img>', {
				src: v.image
			});
			$('#banner51').append(a.append(img));
		} else {
			console.log("NativeOptimize Banner: Nao ha anuncios");
		}
	}).fail(function () {
		console.log('NativeOptimize Banner: Widget não encontrado no sistema.');
	});
});
