$(document).ready(function(){url='http://localhost/native-optimize/public';wg=$('#nativeop51-list').data('wg');if(wg==null){alert('Native Optimize não encontrado.');return}$.ajax({dataType:"json",accepts:"application/json",method:'GET',url:url+'/random_creatives?wg='+wg}).done(function(x){con=$('<div>');ul=$('<ul>').css({'list-style-type':'none','margin':0,'padding':0,});$.each(x,function(i,v){$.post({url:url+'/impressions',dataType:"json",data:{ct:v.hashid,wg:wg}});a=$('<a>',{title:v.name,href:v.url,target:'_blank',click:function(){$.post({url:url+'/clicks',dataType:"json",data:{click_id:v.c_id,ct:v.hashid,wg:wg}})}}).css({'color':'white','text-decoration':'none'});img=$('<img>',{src:url+'/'+v.image,width:240,height:180});li=$('<li>').css({'float':'left'});box1=$('<div>').css({'width':'240px','height':'auto','border':'none','padding':'1px','background-color':'#181818',});box1.append(a.clone(true,true).append(img));box2=$('<div>').css({'background-color':'#a3a3a3','padding':'5px'});brand=$('<p>').css({'color':'white','text-align':'right'});box2.append(a.append(v.name)).append(brand.text(v.brand));li.append(box1.append(box2));ul.append(li)});con.append(ul);$('#nativeop51-list').append(con)})});